import React, { Component } from 'react'
import "./coursesps.css"

 class coursesps extends Component {
     constructor(props) {
         super(props)
     
         this.state = {
              Grade_point:"10",
              Credits:""
         }
     }
     handleGradeChange=(e)=>{
         this.setState({
             Grade_point:e.target.value
         })
     }
     handleCreditChange=(e)=>{
         this.setState({
             Credits:e.target.value
         })
     }
    render() {
        return (
            <div className="course1" >
                 {/* {this.state.Grade_point} {this.state.Credits} */}
                <form>
                    <label className="course_grade_label">
                        Course Grade
                        <select Cvalue={this.state.Grade_point} onChange={this.handleGradeChange }>
                            <option value="10">A*</option>
                            <option value="10">A</option>
                            <option value="8">B</option>
                            <option value="6">C</option>
                            <option value="4">D</option>
                            <option value="2">E</option>
                            <option value="0">F</option>
                            <option value="0">Drop</option>
                        </select>
                    </label>
                    <label >
                        Credit weightage
                        <input type="number" values={this.state.Credits} onChange={this.handleCreditChange}></input>
                    </label>
                </form>
            </div>
        )
    }
}

export default coursesps
