import React, { Component } from 'react'
import './sem_list.css'
class sem_list extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             value:""
        }
    }
    handleChange=(event)=>{
        this.setState({
            value:event.target.value
        })
    }
    render() {
        return (
            <div className ='nocourse'>
               
                <form >
                    <label className='course_no_text'>
                        {"No of Courses in Sem " + this.props.value +"     "}
                        <input className='course_no_input' type="number" max='10' min='1' value={this.state.value} 
                        onChange={function () { this.handleChange()}} onChange={()=>{this.props.onChange(this.state)}}/>
                    </label>
                </form>
            </div>
        )
    }
}

export default sem_list
